import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [quote, setQuote] = useState('');
  const [newQuote, setNewQuote] = useState('');

  // Fonction pour récupérer une citation aléatoire
  const fetchQuote = () => {
    fetch('http://localhost:5000/quotes')
      .then((res) => res.json())
      .then((data) => {
        setQuote(data.quote);
      })
      .catch((err) => console.error("Erreur de fetch :", err));
  };

  // Récupération automatique à l'affichage de la page
  useEffect(() => {
    fetchQuote();
  }, []);

  // Fonction pour ajouter une nouvelle citation
  const handleAddQuote = () => {
    if (newQuote.trim() === '') return;

    fetch('http://localhost:5000/quotes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ quote: newQuote })
    })
      .then((res) => res.json())
      .then((data) => {
        console.log('Citation ajoutée :', data.quote);
        setNewQuote('');
        fetchQuote(); // recharge une nouvelle citation après ajout
      })
      .catch((err) => console.error("Erreur lors de l'ajout :", err));
  };

  return (
    <div className="App" style={{ padding: '2rem', textAlign: 'center' }}>
      <h1>Citation du jour</h1>
      <blockquote style={{ fontStyle: 'italic', margin: '1rem' }}>{quote}</blockquote>

      <button onClick={fetchQuote}>Nouvelle citation</button>

      {/* Formulaire d'ajout */}
      <div style={{ marginTop: '2rem' }}>
        <h2>Ajouter une citation</h2>
        <input
          type="text"
          value={newQuote}
          onChange={(e) => setNewQuote(e.target.value)}
          placeholder="Entrez une nouvelle citation"
          style={{ padding: '0.5rem', width: '300px' }}
        />
        <button
          onClick={handleAddQuote}
          style={{ marginLeft: '1rem', padding: '0.5rem 1rem' }}
        >
          Ajouter
        </button>
      </div>
    </div>
  );
}

export default App;
